import React, { useEffect, useState } from "react";
import ShhoppingCartIcon from "./img/shopping_cart_icon.png";

export default function ProductsArray({ cart, setCart }) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('https://fakestoreapi.com/products/')
      .then(res => res.json())
      .then(json => setProducts(json))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  const handleAddToCart = (productId) => {
    if (!products) {
      console.error('Ошибка: продукты не определены.');
      return;
    }

    const selectedProduct = products.find(product => product.id === productId);

    if (!selectedProduct) {
      console.error('Ошибка: выбранный продукт не найден.');
      return;
    }

    // Проверка, что cart является массивом
    if (!Array.isArray(cart)) {
      console.error('Ошибка: корзина не является массивом.');
      return;
    }

    const productAlreadyInCart = cart.find(item => item.id === productId);

    if (productAlreadyInCart) {
      // Продукт уже в корзине, можете обновить количество или принять другие меры
      // Например, обновление количества:
      setCart(prevCart =>
        prevCart.map(item =>
          item.id === productId ? { ...item, quantity: item.quantity + 1 } : item
        )
      );
    } else {
      // Продукта ещё нет в корзине, добавляем
      setCart(prevCart => [...prevCart, { ...selectedProduct, quantity: 1 }]);
    }
  };

  return (
    <div className="ConteynerProducts">
      {products.map((product) => (
        <div key={product.id} className="Product">
          <div className="AboutProduct">
            <img src={product.image} alt={product.title} />
            <span>{product.title}</span>
            <div className="ProductPrice">
              <span> $ {product.price} </span>
              <button onClick={() => handleAddToCart(product.id)}>
                <img src={ShhoppingCartIcon} alt="Shopping Cart" />
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}
